"""## Data Cleaning"""

# Data Cleaning / Integration
class_mapping = {label:idx for idx, label in enumerate(np.unique(Jack_df['term']))}
Jack_df['term']=Jack_df['term'].map(class_mapping)
class_mapping = {label:idx for idx, label in enumerate(np.unique(Jack_df['grade']))}
Jack_df['grade']=Jack_df['grade'].map(class_mapping)
class_mapping = {label:idx for idx, label in enumerate(np.unique(Jack_df['home_ownership']))}
Jack_df['home_ownership']=Jack_df['home_ownership'].map(class_mapping)

class_mapping = {label:idx for idx, label in enumerate(np.unique(Jack_df['verification_status']))}
Jack_df['verification_status']=Jack_df['verification_status'].map(class_mapping)

class_mapping = {label:idx for idx, label in enumerate(np.unique(Jack_df['purpose']))}
Jack_df['purpose']=Jack_df['purpose'].map(class_mapping)

class_mapping = {label:idx for idx, label in enumerate(np.unique(Jack_df['addr_state']))}
Jack_df['addr_state']=Jack_df['addr_state'].map(class_mapping)

class_mapping = {'Fully Paid' : 0, 'Charged Off' : 1}
Jack_df['loan_status']=Jack_df['loan_status'].map(class_mapping)

Jack_df.head()

# deal with missing value
Jack_df = Jack_df.select_dtypes(include=[np.number]).interpolate().dropna()
# drop high correlation and high variance colums
Jack_df = Jack_df.drop(["total_pymnt"], axis=1)
Jack_df = Jack_df.drop(["total_pymnt_inv"], axis=1)
Jack_df = Jack_df.drop(["total_rec_int"], axis=1)
Jack_df = Jack_df.drop(["id"], axis=1)
Jack_df = Jack_df.drop(["total_rec_prncp"], axis=1)

Jack_df.head(10)

"""## Data Split"""

# split dataset
from sklearn.model_selection import train_test_split

yPredict = Jack_df.loan_status
XClean = Jack_df.drop(["loan_status"], axis=1)

# Reserve 25% for testing
